$(document).ready((function(){$("#btn1").click((function(e){e.preventDefault(),$(".lp").css("display","block");const t=document.getElementById("inpt1").value.trim();document.querySelector(".js-search-results").innerHTML="";try{const e=`https://en.wikipedia.org/w/api.php?action=query&list=search&prop=info&inprop=url&utf8=&format=json&origin=*&srlimit=20&srsearch=${t}`;fetch(e).then((e=>e.json())).then((e=>{0===e.query.searchinfo.totalhits?alert("Sorry No Results Found! Try a different search string"):e.query.search.forEach((e=>{const t=`https://en.wikipedia.org/?curid=${e.pageid}`;$(".js-search-results").append(`<div class="result-item m-3">\n                      <h4 class="result-title">\n                        <a href="${t}" target="_blank" rel="noopener">${e.title}</a>\n                      </h4>\n                      <a href="${t}" class="result-link" target="_blank" rel="noopener"><b class="text-success">${t}</b></a>\n                      <span class="result-snippet text-secondary">${e.snippet}</span><br>\n                    </div>`),$(".lp").css("display","none")}))}))}catch(e){}$("#inpt1").val("")}))}));