$(document).ready((function(){$("#ip").prepend(` <h5> <i class="far fa-clock"></i> ${Date()}</h5>`),url1="https://freegeoip.app/json/",fetch(url1).then((e=>e.json())).then((e=>{let t=e;$("#geol").prepend(`<h5>Your Location Is Detected As</h5><h3> <i class="fas fa-street-view"></i> ${t.city+"-"+t.region_name}</h3>`);const a=t.city;fetch(`https://api.openweathermap.org/data/2.5/weather?q=${a}&appid=0926e3715bd414c4bdd946435fb4553d&units=metric`).then((e=>e.json())).then((e=>{$("#weather").prepend(`<h5>Current Weather at your location is :</h5><h4> <i class="fas fa-cloud-sun-rain"></i> ${e.main.temp+" <span>&#8451;</span>"}</h4>`)})),url5="https://type.fit/api/quotes",fetch(url5).then((e=>e.json())).then((function(e){var t=Math.floor(100*Math.random()+1);$("#qut").prepend(`<b>Quote For the Day</b> <i class="fas fa-users text-dark"></i><br><h6>Author - ${e[t].author}</h6> <h4> ${e[t].text}</h4>`)}))}))}));